glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in mediump vec2 a_uv0;
out mediump vec2 v_uv0;
in lowp vec4 a_color;
out lowp vec4 v_color;
void main () {
  mat4 mvp = cc_matViewProj;
  v_uv0 = a_uv0;
  v_color = a_color;
  gl_Position = mvp * vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in mediump vec2 v_uv0;
in lowp vec4 v_color;
uniform sampler2D texture;
uniform inputData {
  float blur;
};
void main () {
  float offset = 0.00078125 * blur;
  vec4 color = vec4(0.);
  color += texture2D(texture, vec2(v_uv0.x - offset, v_uv0.y - offset)) * 0.0947416;
  color += texture2D(texture, vec2(v_uv0.x,          v_uv0.y - offset)) * 0.118318;
  color += texture2D(texture, vec2(v_uv0.x + offset, v_uv0.y + offset)) * 0.0947416;
  color += texture2D(texture, vec2(v_uv0.x - offset, v_uv0.y)) * 0.118318;
  color += texture2D(texture, vec2(v_uv0.x,          v_uv0.y)) * 0.147761;
  color += texture2D(texture, vec2(v_uv0.x + offset, v_uv0.y)) * 0.118318;
  color += texture2D(texture, vec2(v_uv0.x - offset, v_uv0.y + offset)) * 0.0947416;
  color += texture2D(texture, vec2(v_uv0.x,          v_uv0.y + offset)) * 0.118318;
  color += texture2D(texture, vec2(v_uv0.x + offset, v_uv0.y - offset)) * 0.0947416;
  gl_FragColor = color;
}

